import React from 'react';
import { useEffect, useState } from 'react';
import MovieCard from './MovieCard';
import './App.css';
import SearchIcon from './search.svg';
const API_URL = 'http://www.omdbapi.com/?apikey=e38ec450&s';
const movie = {
    "Title": "Good Will Hunting", 
    "Year": "1997", 
    "imdbID": "tt0119217", 
    "Type": "movie", 
    "Poster": "https://m.media-amazon.com/images/M/MV5BOTI0MzcxMTYtZDVkMy00NjY1LTgyMTYtZmUxN2M3NmQ2NWJhXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg"
}
const App = () => {
    const [movies, setMovies] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const searchMovies = async (title) => {
        const response = await fetch(`${API_URL}&s=${title}`);
        const data = await response.json();
        setMovies(data.Search);
    }
    useEffect(() => {
        searchMovies('Good Will Hunting');
    }, []);
    return (
        <div className="App">  
            <h1>Movieland</h1>
            <div className="search">
                <input 
                type="text" 
                placeholder="Search for a movie" 
                value={searchTerm}
                onChange={(e)=>setSearchTerm(e.target.value)}
                />
                <img 
                src={SearchIcon} 
                alt="Search" 
                onClick={()=>searchMovies(searchTerm)}
                />
            </div>

            {movies?.length > 0
            ? (
                <div className="container">
                {movies.map((movie) => (
                    <MovieCard movie={movie}/>
                    ))}
                </div>
            ): (
                <div className="empty">
                    <h2>No movies found</h2>
                </div>
                )
            }
        </div>
    );
}

export default App;
